import React, { useState, useContext, useEffect } from "react";
import BanckQuest from './BankQuest';
import "./Admin.css";
import { DataContext } from "../contexts/dataContext";

const Admin = () => {
  const[variant, setVariant] = useState('')
  const handleClickTab = (type: string) => () => {
    setVariant(type)
  };
  const {
    data, data: { questions }, dataService
  } = useContext(DataContext);

  useEffect(() => {
    dataService.getQuestions()
  },[])
  const questType = questions.map((q: any) => {
    let arr: any = []
    if(!arr.includes(q.type)) {
      arr.push(q.type)
    }
    return arr;
  })
  console.log(questType)
  return (
    <div className="Admin">
      <div className="Admin-side">
          <div className="Admin-tab Admin-tabs"  >Questions Groups</div>
          {questType.map((q: any) => 
          <div key={q} className="Admin-tab" onClick={handleClickTab(q)}>{q}</div>)}
      </div>
      <div className="Admin-content" >
       {variant && <BanckQuest variant={variant} />}
      </div>
    </div>
  );
};

export default Admin;
