//import * as Questions from '../db.json';


export interface IQuestion {
  id?: string;
  type: string;
  question: string;
  options: string[];
  answer: string;
}

export interface DataState {
    questions: IQuestion;
}

export const initialDataState: DataState = {
  questions: any
};
export type DataAction = 
| { type: "FETCH_QUESTIONS"; payload: IQuestion[] }
| { type: "ADD_QUESTIONS"; payload: IQuestion };

export const dataReducer = (state: DataState, action: DataAction) => {
  switch (action.type) {
    case "FETCH_QUESTIONS":
      return { ...state, questions: action.payload };
    case "ADD_QUESTIONS":
      return { ...state, questions: state.questions.concat(action.payload) };
    default:
      return state;
  }
};
